using System;using System.Collections;using System.Collections.Generic;using UnityEngine;public class Anima_Rotate : MonoBehaviour{    public float speed = 1;    Vector3 TempRotate;    private void Awake()    {        TempRotate = transform.localEulerAngles;    }    private void OnEnable()    {        transform.localEulerAngles = TempRotate;    }    private void Update()    {        transform.Rotate(Vector3.back * speed * Time.deltaTime);    }}